import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Spinner, Alert, Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    axios.get('http://localhost:5000/api/movies')
      .then(res => {
        setMovies(Array.isArray(res.data) ? res.data : []);
        setLoading(false);
      })
      .catch(() => {
        setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch phim t·ª´ m√°y ch·ªß!');
        setLoading(false);
      });
  }, []);

  // L·ªçc phim theo t√™n
  const filteredMovies = movies.filter(movie =>
    movie.title?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container className="mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1 className="text-center mb-0">üé¨ ·ª®ng d·ª•ng v√© xem phim</h1>
        {token && role === 'Admin' && (
          <Button variant="success" onClick={() => navigate('/admin')}>
            ‚ûï Th√™m phim
          </Button>
        )}
      </div>
      <Form className="mb-4">
        <Form.Control
          type="text"
          placeholder="T√¨m ki·∫øm phim theo t√™n..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
      </Form>
      <h4 className="mb-4 text-center text-secondary">Danh s√°ch phim n·ªïi b·∫≠t</h4>
      {loading && (
        <div className="d-flex justify-content-center my-5">
          <Spinner animation="border" variant="primary" />
        </div>
      )}
      {error && <Alert variant="danger" className="text-center">{error}</Alert>}
      <Row>
        {filteredMovies.map(movie => (
          <Col md={4} sm={6} xs={12} className="mb-4" key={movie.movie_id}>
            <Card className="h-100 shadow-sm d-flex flex-column">
              <div style={{ width: '100%', height: 220, overflow: 'hidden', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <Card.Img
                  variant="top"
                  src={
                    movie.image
                      ? `http://localhost:5000${movie.image}`
                      : "https://via.placeholder.com/400x220?text=Movie+Poster"
                  }
                  alt={movie.title}
                  style={{ maxHeight: 220, width: 'auto', objectFit: 'contain' }}
                />
              </div>
              <Card.Body className="d-flex flex-column">
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text>
                  <strong>Th·ªÉ lo·∫°i:</strong> {movie.genre}<br />
                  <strong>ƒê·∫°o di·ªÖn:</strong> {movie.director}
                </Card.Text>
                <div className="mt-auto">
                  <Button
                    variant="info"
                    className="me-2 mb-2"
                    onClick={() => navigate(`/movies/${movie.movie_id}`)}
                  >
                    Chi ti·∫øt
                  </Button>
                  {token && role === 'Admin' && (
                    <>
                      <Button
                        variant="warning"
                        className="me-2 mb-2"
                        onClick={() => navigate(`/edit-movie/${movie.movie_id}`)}
                      >
                        S·ª≠a
                      </Button>
                      <Button
                        variant="danger"
                        className="mb-2"
                        onClick={() => navigate(`/admin?delete=${movie.movie_id}`)}
                      >
                        X√≥a
                      </Button>
                    </>
                  )}
                </div>
              </Card.Body>
              <Card.Footer className="text-center">
                <small className="text-muted">
                  Kh·ªüi chi·∫øu: {movie.release_date ? new Date(movie.release_date).toLocaleDateString() : ''}
                </small>
              </Card.Footer>
            </Card>
          </Col>
        ))}
      </Row>
      {(!loading && filteredMovies.length === 0) && (
        <Alert variant="info" className="text-center">Kh√¥ng t√¨m th·∫•y phim n√†o ph√π h·ª£p.</Alert>
      )}
    </Container>
  );
};

export default HomePage;
