import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form, Container, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5000/api/movies';

const AdminPage = () => {
  const [newMovie, setNewMovie] = useState({
    title: '',
    genre: '',
    director: '',
    main_actor: '',
    duration: '',
    language: '',
    country: '',
    release_date: '',
    description: '',
    subtitle: '',
    age: '',
    content: ''
  });
  const [image, setImage] = useState(null);
  const [video, setVideo] = useState(null);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setNewMovie({ ...newMovie, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      const formData = new FormData();
      Object.keys(newMovie).forEach(key => {
        formData.append(key, newMovie[key]);
      });
      if (image) formData.append('image', image);
      if (video) formData.append('video', video);

      const res = await axios.post(API_URL, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });

      setMessage(res.data.message || 'Thêm phim thành công!');
      setNewMovie({
        title: '',
        genre: '',
        director: '',
        main_actor: '',
        duration: '',
        language: '',
        country: '',
        release_date: '',
        description: '',
        subtitle: '',
        age: '',
        content: ''
      });
      setImage(null);
      setVideo(null);
    } catch (err) {
      console.error(err);
      setMessage('Đã xảy ra lỗi khi thêm phim.');
    }
  };

  return (
    <Container className="mt-5">
      <h2 className="mb-4 text-center">📝 Thêm Phim Mới</h2>
      {message && <Alert variant="info">{message}</Alert>}
      <Form onSubmit={handleSubmit} encType="multipart/form-data">
        <Form.Group className="mb-3">
          <Form.Label>Tiêu đề</Form.Label>
          <Form.Control name="title" value={newMovie.title} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Thể loại</Form.Label>
          <Form.Control name="genre" value={newMovie.genre} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Đạo diễn</Form.Label>
          <Form.Control name="director" value={newMovie.director} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Diễn viên chính</Form.Label>
          <Form.Control name="main_actor" value={newMovie.main_actor} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Thời lượng (phút)</Form.Label>
          <Form.Control type="number" name="duration" value={newMovie.duration} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Ngôn ngữ</Form.Label>
          <Form.Control name="language" value={newMovie.language} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Quốc gia</Form.Label>
          <Form.Control name="country" value={newMovie.country} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Ngày khởi chiếu</Form.Label>
          <Form.Control type="date" name="release_date" value={newMovie.release_date} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Mô tả</Form.Label>
          <Form.Control as="textarea" name="description" value={newMovie.description} onChange={handleChange} rows={3} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Phụ đề</Form.Label>
          <Form.Control name="subtitle" value={newMovie.subtitle} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Giới hạn tuổi</Form.Label>
          <Form.Control type="number" name="age" value={newMovie.age} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Nội dung</Form.Label>
          <Form.Control as="textarea" name="content" value={newMovie.content} onChange={handleChange} rows={4} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Ảnh đại diện (Poster)</Form.Label>
          <Form.Control type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        </Form.Group>

        <Form.Group className="mb-4">
          <Form.Label>Video Trailer (mp4)</Form.Label>
          <Form.Control type="file" accept="video/mp4" onChange={(e) => setVideo(e.target.files[0])} />
        </Form.Group>

        <div className="text-center">
          <Button type="submit" variant="primary">Thêm Phim</Button>
          <Button variant="secondary" className="ms-3" onClick={() => navigate('/')}>Quay lại</Button>
        </div>
      </Form>
    </Container>
  );
};

export default AdminPage;
