// AdminPage.js - Thêm phim mới
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5000/api/movies';

const AdminPage = () => {
  const [newMovie, setNewMovie] = useState({
    title: '', genre: '', director: '', main_actor: '', duration: '', language: '',
    country: '', release_date: '', description: '', subtitle: '', age: '', content: '',
    image: null, video: null
  });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (!token || role !== 'Admin') navigate('/');
  }, [navigate]);

  const handleChange = e => {
    const { name, value, files } = e.target;
    setNewMovie(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleSubmit = async () => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    Object.entries(newMovie).forEach(([key, val]) => {
      if (val) formData.append(key, val);
    });

    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { Authorization: `Bearer ${token}` },
        body: formData
      });
      const data = await res.json();
      if (res.ok) {
        alert('Thêm phim thành công');
        setNewMovie({ title: '', genre: '', director: '', main_actor: '', duration: '', language: '', country: '', release_date: '', description: '', subtitle: '', age: '', content: '', image: null, video: null });
      } else {
        alert(data.error || 'Lỗi khi thêm phim');
      }
    } catch (err) {
      alert('Lỗi mạng hoặc máy chủ.');
    }
  };

  return (
    <div className="container py-4">
      <h2 className="mb-4">Thêm phim mới</h2>
      <div className="row g-2">
        {['title', 'genre', 'director', 'main_actor', 'duration', 'language', 'country'].map(field => (
          <div className="col-md-6" key={field}>
            <input
              className="form-control mb-2"
              name={field}
              placeholder={field.replace('_', ' ').toUpperCase()}
              value={newMovie[field] || ''}
              onChange={handleChange}
            />
          </div>
        ))}
        <div className="col-md-6">
          <input type="date" className="form-control mb-2" name="release_date" value={newMovie.release_date} onChange={handleChange} />
        </div>
        {['subtitle', 'age'].map(field => (
          <div className="col-md-6" key={field}>
            <input className="form-control mb-2" name={field} placeholder={field.toUpperCase()} value={newMovie[field]} onChange={handleChange} />
          </div>
        ))}
        {['description', 'content'].map(field => (
          <div className="col-12" key={field}>
            <textarea className="form-control mb-2" name={field} placeholder={field.toUpperCase()} value={newMovie[field]} onChange={handleChange} rows={2} />
          </div>
        ))}
        <div className="col-md-6">
          <input type="file" className="form-control mb-2" name="image" accept="image/*" onChange={handleChange} />
        </div>
        <div className="col-md-6">
          <input type="file" className="form-control mb-2" name="video" accept="video/*" onChange={handleChange} />
        </div>
      </div>
      <button className="btn btn-success mt-3" onClick={handleSubmit}>Thêm phim</button>
    </div>
  );
};

export default AdminPage;
