import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Container, Button, Spinner, Alert, Row, Col, Card } from 'react-bootstrap';

const MovieDetailPage = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/movies/${id}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Không tìm thấy phim!');
        setLoading(false);
      });
  }, [id]);

  if (loading) return <Spinner animation="border" className="d-block mx-auto mt-5" />;
  if (error) return <Alert variant="danger" className="mt-5 text-center">{error}</Alert>;
  if (!movie) return null;

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={10} lg={8}>
          <Card className="shadow-lg border-0">
            <Row className="g-0">
              <Col md={5} className="d-flex flex-column align-items-center justify-content-center p-3">
                <div style={{
                  width: '100%',
                  height: 320,
                  overflow: 'hidden',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  borderRadius: 10,
                  background: '#f8f9fa'
                }}>
                  <img
                    src={movie.image ? `http://localhost:5000${movie.image}` : "https://via.placeholder.com/400x320?text=Movie+Poster"}
                    alt={movie.title}
                    style={{ maxHeight: 320, width: 'auto', objectFit: 'contain' }}
                  />
                </div>
                {/* Hiển thị video trailer nếu có */}
                {movie.video && (
                  <div className="mt-3 w-100">
                    <h5 className="text-center">Trailer</h5>
                    <video width="100%" height="200" controls style={{ borderRadius: 8, background: '#000' }}>
                      <source src={`http://localhost:5000${movie.video}`} type="video/mp4" />
                      Trình duyệt không hỗ trợ video.
                    </video>
                  </div>
                )}
              </Col>
              <Col md={7} className="p-4 d-flex flex-column justify-content-between">
                <div>
                  <h2 className="mb-3 text-primary">{movie.title}</h2>
                  <p><strong>Thể loại:</strong> {movie.genre}</p>
                  <p><strong>Đạo diễn:</strong> {movie.director}</p>
                  <p><strong>Khởi chiếu:</strong> {movie.release_date ? new Date(movie.release_date).toLocaleDateString() : ''}</p>
                  <p><strong>Mô tả:</strong> {movie.description || 'Chưa có mô tả.'}</p>
                </div>
                <div className="mt-4 d-flex flex-wrap gap-3">
                  <Button variant="primary" onClick={() => navigate(`/booking/${movie.movie_id}`)}>
                    Đặt vé
                  </Button>
                  <Button variant="secondary" onClick={() => navigate(-1)}>
                    Quay lại
                  </Button>
                </div>
              </Col>
            </Row>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default MovieDetailPage;
