// src/pages/AdminPage.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminPage = () => {
  const [movies, setMovies] = useState([]);
  const [newMovie, setNewMovie] = useState({ title: '', description: '' });
  const [editingMovie, setEditingMovie] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const role = localStorage.getItem('role');
    if (role !== 'admin') {
      navigate('/');
    } else {
      fetchMovies();
    }
  }, [navigate]);

  const fetchMovies = async () => {
    const res = await fetch('/api/movies');
    const data = await res.json();
    setMovies(data);
  };

  const handleAddMovie = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch('/api/movies', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify(newMovie),
    });
    if (res.ok) {
      setNewMovie({ title: '', description: '' });
      fetchMovies();
    }
  };

  const handleUpdateMovie = async () => {
    const token = localStorage.getItem('token');
    const res = await fetch(`/api/movies/${editingMovie.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify(editingMovie),
    });
    if (res.ok) {
      setEditingMovie(null);
      fetchMovies();
    }
  };

  const handleDeleteMovie = async (id) => {
    const token = localStorage.getItem('token');
    const res = await fetch(`/api/movies/${id}`, {
      method: 'DELETE',
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    });
    if (res.ok) fetchMovies();
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Quản lý phim</h2>

      <div>
        <input
          value={newMovie.title}
          onChange={(e) => setNewMovie({ ...newMovie, title: e.target.value })}
          placeholder="Tên phim"
        />
        <input
          value={newMovie.description}
          onChange={(e) => setNewMovie({ ...newMovie, description: e.target.value })}
          placeholder="Mô tả"
        />
        <button onClick={handleAddMovie}>Thêm phim</button>
      </div>

      <ul style={{ marginTop: '2rem' }}>
        {movies.map((movie) => (
          <li key={movie.id}>
            {editingMovie && editingMovie.id === movie.id ? (
              <>
                <input
                  value={editingMovie.title}
                  onChange={(e) => setEditingMovie({ ...editingMovie, title: e.target.value })}
                />
                <input
                  value={editingMovie.description}
                  onChange={(e) => setEditingMovie({ ...editingMovie, description: e.target.value })}
                />
                <button onClick={handleUpdateMovie}>Lưu</button>
                <button onClick={() => setEditingMovie(null)}>Hủy</button>
              </>
            ) : (
              <>
                <strong>{movie.title}</strong> - {movie.description}
                <button onClick={() => setEditingMovie(movie)}>Sửa</button>
                <button onClick={() => handleDeleteMovie(movie.id)}>Xóa</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPage;
