import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Container, Button, Spinner, Alert, Row, Col, Card, Modal } from 'react-bootstrap';
import { FaRegLightbulb, FaLightbulb, FaPlayCircle } from 'react-icons/fa';

const MovieDetailPage = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const [showTrailer, setShowTrailer] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/movies/${id}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Không tìm thấy phim!');
        setLoading(false);
      });
  }, [id]);

  const themeBg = darkMode ? "#23272f" : "#181c2b";
  const themeText = darkMode ? "#fff" : "#fff";
  const cardBg = darkMode ? "#181a20" : "#23244a";
  const cardShadow = darkMode ? "0 2px 16px rgba(0,0,0,0.5)" : "0 2px 8px rgba(0,0,0,0.07)";

  if (loading) return <Spinner animation="border" className="d-block mx-auto mt-5" />;
  if (error) return <Alert variant="danger" className="mt-5 text-center">{error}</Alert>;
  if (!movie) return null;

  return (
    <div
      style={{
        background: themeBg,
        minHeight: "100vh",
        color: themeText,
        transition: "all 0.3s"
      }}
    >
      {/* Nút bóng đèn chuyển theme */}
      <div style={{
        position: 'fixed',
        top: 24,
        right: 36,
        zIndex: 1000,
        cursor: 'pointer',
        fontSize: 28,
        color: darkMode ? "#ffd600" : "#fff"
      }}
        title={darkMode ? "Chuyển sang chế độ sáng" : "Chuyển sang chế độ tối"}
        onClick={() => setDarkMode(!darkMode)}
      >
        {darkMode ? <FaLightbulb /> : <FaRegLightbulb />}
      </div>

      <Container className="mt-4">
        <Row className="justify-content-center">
          <Col md={11} lg={10}>
            <Card
              className="shadow-lg border-0 p-4"
              style={{
                background: cardBg,
                color: themeText,
                transition: "all 0.3s",
                boxShadow: cardShadow,
                borderRadius: 18
              }}
            >
              <Row>
                {/* Poster bên trái */}
                <Col md={4} className="mb-3 mb-md-0 d-flex flex-column align-items-center">
                  <div style={{
                    width: '100%',
                    minHeight: 480,
                    overflow: 'hidden',
                    borderRadius: 16,
                    background: "#fff",
                    boxShadow: cardShadow,
                    marginBottom: 12
                  }}>
                    <img
                      src={movie.image ? `http://localhost:5000${movie.image}` : "https://via.placeholder.com/400x600?text=Movie+Poster"}
                      alt={movie.title}
                      style={{ width: '100%', height: 480, objectFit: 'cover', display: 'block' }}
                    />
                  </div>
                </Col>
                {/* Thông tin bên phải */}
                <Col md={8}>
                  <div style={{ padding: '0 10px' }}>
                    <h1 style={{ fontWeight: 900, fontSize: 38, marginBottom: 18 }}>{movie.title}</h1>
                    <div className="mb-3" style={{ fontSize: 18 }}>
                      <span style={{ marginRight: 18 }}>
                        <i className="bi bi-film" /> {movie.genre}
                      </span>
                      <span style={{ marginRight: 18 }}>
                        <i className="bi bi-clock" /> {movie.duration || "125'"} {/* duration nếu có */}
                      </span>
                      <span style={{ marginRight: 18 }}>
                        <i className="bi bi-translate" /> {movie.subtitle || "Phụ Đề"}
                      </span>
                      <span style={{ marginRight: 18 }}>
                        <i className="bi bi-person-badge" /> <span style={{ background: "#ffe600", color: "#222", padding: "2px 8px", borderRadius: 4, fontWeight: 700 }}>T16: Phim dành cho khán giả từ đủ 16 tuổi trở lên (16+)</span>
                      </span>
                    </div>
                    <div className="mb-3" style={{ fontSize: 17 }}>
                      <div style={{ fontWeight: 700, fontSize: 20, marginBottom: 6 }}>MÔ TẢ</div>
                      <div>
                        <span style={{ fontWeight: 500 }}>Đạo diễn:</span> {movie.director}
                        <br />
                        <span style={{ fontWeight: 500 }}>Khởi chiếu:</span> {movie.release_date ? new Date(movie.release_date).toLocaleDateString() : ''}
                      </div>
                    </div>
                    <div className="mb-4" style={{ fontSize: 17 }}>
                      <div style={{ fontWeight: 700, fontSize: 20, marginBottom: 6 }}>NỘI DUNG PHIM</div>
                      <div>{movie.description || 'Chưa có mô tả.'}</div>
                    </div>
                    {/* Nút xem trailer */}
                    {movie.video && (
                      <div className="mb-4">
                        <Button
                          variant="outline-light"
                          style={{
                            fontWeight: 600,
                            fontSize: 18,
                            borderRadius: 30,
                            padding: "8px 28px",
                            background: "#23244a",
                            color: "#fff",
                            boxShadow: "0 2px 8px rgba(0,0,0,0.10)"
                          }}
                          onClick={() => setShowTrailer(true)}
                        >
                          <FaPlayCircle style={{ fontSize: 24, marginRight: 8, color: "#e74c3c" }} />
                          Xem Trailer
                        </Button>
                      </div>
                    )}
                    <div className="mt-4 d-flex flex-wrap gap-3">
                      <Button variant="primary" onClick={() => navigate(`/booking/${movie.movie_id}`)}>
                        Đặt vé
                      </Button>
                      <Button variant="secondary" onClick={() => navigate(-1)}>
                        Quay lại
                      </Button>
                    </div>
                  </div>
                </Col>
              </Row>
            </Card>
          </Col>
        </Row>
      </Container>

      {/* Modal Trailer */}
      <Modal
        show={showTrailer}
        onHide={() => setShowTrailer(false)}
        centered
        size="lg"
        contentClassName={darkMode ? "bg-dark" : ""}
      >
        <Modal.Header closeButton closeVariant={darkMode ? "white" : undefined}>
          <Modal.Title>Trailer: {movie.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body style={{ background: darkMode ? "#181a20" : "#fff" }}>
          <video
            width="100%"
            height="420"
            controls
            autoPlay
            style={{ borderRadius: 10, background: '#000', display: 'block' }}
          >
            <source src={`http://localhost:5000${movie.video}`} type="video/mp4" />
            Trình duyệt không hỗ trợ video.
          </video>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default MovieDetailPage;
