import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Card, Spinner, Alert, Button, Form, Badge } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  useEffect(() => {
    axios.get('http://localhost:5000/api/movies')
      .then(res => {
        setMovies(Array.isArray(res.data) ? res.data : []);
        setLoading(false);
      })
      .catch(() => {
        setError('Không thể tải danh sách phim từ máy chủ!');
        setLoading(false);
      });
  }, []);

  // Lọc phim theo tên
  const filteredMovies = movies.filter(movie =>
    movie.title?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ background: "#f4f6fb", minHeight: "100vh", paddingBottom: 40 }}>
      <Container className="pt-4">
        <div className="d-flex flex-column flex-md-row justify-content-between align-items-center mb-4 gap-3">
          <h1 className="mb-0 fw-bold" style={{ color: "#2d3a4b", letterSpacing: 1 }}>
            🎬 Movie Ticket App
          </h1>
          {token && role === 'Admin' && (
            <Button variant="success" size="lg" onClick={() => navigate('/admin')}>
              ➕ Thêm phim
            </Button>
          )}
        </div>
        <Form className="mb-4 mx-auto" style={{ maxWidth: 500 }}>
          <Form.Control
            type="text"
            placeholder="🔎 Tìm kiếm phim theo tên..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            style={{ fontSize: 18, borderRadius: 30, padding: "12px 24px" }}
          />
        </Form>
        <h4 className="mb-4 text-center text-secondary fw-semibold" style={{ letterSpacing: 1 }}>
          Danh sách phim nổi bật
        </h4>
        {loading && (
          <div className="d-flex justify-content-center my-5">
            <Spinner animation="border" variant="primary" />
          </div>
        )}
        {error && <Alert variant="danger" className="text-center">{error}</Alert>}
        <Row>
          {filteredMovies.map(movie => (
            <Col lg={4} md={6} sm={12} className="mb-4" key={movie.movie_id}>
              <Card className="h-100 shadow-sm border-0 rounded-4 d-flex flex-column" style={{ background: "#fff" }}>
                <div
                  style={{
                    width: '100%',
                    height: 260,
                    overflow: 'hidden',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    borderTopLeftRadius: 20,
                    borderTopRightRadius: 20,
                    background: "#e3eafc"
                  }}
                >
                  <Card.Img
                    variant="top"
                    src={
                      movie.image
                        ? `http://localhost:5000${movie.image}`
                        : "https://via.placeholder.com/400x260?text=Movie+Poster"
                    }
                    alt={movie.title}
                    style={{ maxHeight: 260, width: 'auto', objectFit: 'contain', borderRadius: 0 }}
                  />
                </div>
                <Card.Body className="d-flex flex-column">
                  <div className="d-flex align-items-center mb-2 gap-2">
                    <Card.Title className="mb-0 fw-bold" style={{ fontSize: 22 }}>{movie.title}</Card.Title>
                    {movie.age && (
                      <Badge bg="warning" text="dark" style={{ fontSize: 14, fontWeight: 600 }}>
                        {movie.age}
                      </Badge>
                    )}
                  </div>
                  <Card.Text className="mb-1" style={{ fontSize: 16 }}>
                    <span className="text-muted">🎭 {movie.genre}</span>
                  </Card.Text>
                  <Card.Text className="mb-1" style={{ fontSize: 16 }}>
                    <span className="text-muted">🎬 {movie.director}</span>
                  </Card.Text>
                  <Card.Text className="mb-1" style={{ fontSize: 15 }}>
                    <span className="text-muted">⏱ {movie.duration} phút</span>
                  </Card.Text>
                  <Card.Text className="mb-2" style={{ fontSize: 15 }}>
                    <span className="text-muted">🈂️ {movie.subtitle}</span>
                  </Card.Text>
                  <div className="mt-auto d-flex flex-wrap gap-2">
                    <Button
                      variant="info"
                      className="me-2 mb-2"
                      style={{ fontWeight: 600, borderRadius: 20, minWidth: 90 }}
                      onClick={() => navigate(`/movies/${movie.movie_id}`)}
                    >
                      Chi tiết
                    </Button>
                    {token && role === 'Admin' && (
                      <>
                        <Button
                          variant="warning"
                          className="me-2 mb-2"
                          style={{ fontWeight: 600, borderRadius: 20, minWidth: 90 }}
                          onClick={() => navigate(`/edit-movie/${movie.movie_id}`)}
                        >
                          Sửa
                        </Button>
                        <Button
                          variant="danger"
                          className="mb-2"
                          style={{ fontWeight: 600, borderRadius: 20, minWidth: 90 }}
                          onClick={() => navigate(`/admin?delete=${movie.movie_id}`)}
                        >
                          Xóa
                        </Button>
                      </>
                    )}
                  </div>
                </Card.Body>
                <Card.Footer className="text-center bg-white border-0 pb-3 pt-0">
                  <small className="text-muted">
                    📅 Khởi chiếu: {movie.release_date ? new Date(movie.release_date).toLocaleDateString() : ''}
                  </small>
                </Card.Footer>
              </Card>
            </Col>
          ))}
        </Row>
        {(!loading && filteredMovies.length === 0) && (
          <Alert variant="info" className="text-center mt-5">Không tìm thấy phim nào phù hợp.</Alert>
        )}
      </Container>
    </div>
  );
};

export default HomePage;
