import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Button, Alert } from 'react-bootstrap';

const ROWS = 5;
const COLS = 8;

const BookingPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [error, setError] = useState('');

  // Ghế đôi: hàng đầu tiên (A), mỗi ghế đôi chiếm 2 ghế (A1-A2, A3-A4, ...)
  const seats = [];
  for (let row = 0; row < ROWS; row++) {
    for (let col = 1; col <= COLS; col++) {
      // Ghế đôi ở hàng A, cột lẻ (A1, A3, A5, A7)
      if (row === 0 && col % 2 === 1) {
        seats.push({ label: `A${col}/A${col + 1}`, type: 'double', key: `A${col}` });
      }
      // Bỏ qua cột chẵn ở hàng A (vì đã là ghế đôi)
      else if (row === 0 && col % 2 === 0) {
        continue;
      }
      // Các hàng còn lại là ghế đơn
      else if (row > 0) {
        seats.push({ label: String.fromCharCode(65 + row) + col, type: 'single', key: String.fromCharCode(65 + row) + col });
      }
    }
  }

  const toggleSeat = (seat) => {
    setError('');
    if (seat.type === 'double') {
      // Nếu đã chọn, bỏ chọn cả 2 ghế; nếu chưa, chọn cả 2 ghế
      const [g1, g2] = seat.label.split('/');
      if (selectedSeats.includes(seat.label)) {
        setSelectedSeats(selectedSeats.filter(s => s !== seat.label));
      } else {
        setSelectedSeats([...selectedSeats, seat.label]);
      }
    } else {
      setSelectedSeats((prev) =>
        prev.includes(seat.label)
          ? prev.filter((s) => s !== seat.label)
          : [...prev, seat.label]
      );
    }
  };

  const handleBooking = () => {
    if (selectedSeats.length === 0) {
      setError('Vui lòng chọn ít nhất 1 ghế!');
      return;
    }
    // Có thể gửi selectedSeats lên backend ở đây nếu muốn
    navigate(`/payment/${id}`, { state: { seats: selectedSeats } });
  };

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Chọn chỗ ngồi cho phim #{id}</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <div style={{ display: 'inline-block', background: '#222', padding: 20, borderRadius: 8 }}>
        <div className="text-center mb-2" style={{ color: '#fff' }}>Màn hình</div>
        <div style={{ display: 'grid', gridTemplateColumns: `repeat(${COLS}, 40px)`, gap: 10 }}>
          {seats.map((seat, idx) => (
            <Button
              key={seat.key}
              variant={selectedSeats.includes(seat.label) ? (seat.type === 'double' ? 'warning' : 'success') : (seat.type === 'double' ? 'outline-warning' : 'outline-light')}
              style={{
                width: seat.type === 'double' ? 90 : 40,
                height: 40,
                padding: 0,
                gridColumn: seat.type === 'double' ? `span 2` : undefined
              }}
              onClick={() => toggleSeat(seat)}
            >
              {seat.type === 'double' ? '👫 ' : ''}
              {seat.label}
            </Button>
          ))}
        </div>
      </div>
      <div className="mt-3">
        <strong>Ghế đã chọn:</strong> {selectedSeats.join(', ') || 'Chưa chọn'}
      </div>
      <Button className="mt-4" variant="primary" onClick={handleBooking}>
        Tiếp tục thanh toán
      </Button>
    </Container>
  );
};

export default BookingPage;
