import React from 'react';
import { useParams } from 'react-router-dom';
import { Container } from 'react-bootstrap';

const PaymentPage = () => {
  const { id } = useParams();

  return (
    <Container className="mt-4">
      <h2>Thanh toán cho phim #{id}</h2>
      {/* Thêm form nhập thông tin thanh toán, xác nhận, ... */}
      <p>Chức năng thanh toán sẽ được phát triển tiếp...</p>
    </Container>
  );
};

export default PaymentPage;
