import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Button } from 'react-bootstrap';

const BookingPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  // Bạn có thể thêm form chọn ghế, số lượng vé ở đây

  const handleBooking = () => {
    // Xử lý đặt vé ở đây (nếu cần)
    navigate(`/payment/${id}`);
  };

  return (
    <Container className="mt-4">
      <h2>Đặt vé cho phim #{id}</h2>
      {/* Thêm form chọn ghế, số lượng vé, ... */}
      <Button variant="success" onClick={handleBooking}>Tiếp tục thanh toán</Button>
    </Container>
  );
};

export default BookingPage;
