import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Button, Alert } from 'react-bootstrap';

const ROWS = 5; // A-E
const COLS = 8; // 8 gh·∫ø m·ªói h√†ng

const BookingPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [error, setError] = useState('');

  // Gh·∫ø ƒë√¥i ·ªü h√†ng cu·ªëi (E), m·ªói gh·∫ø ƒë√¥i chi·∫øm 2 gh·∫ø (E1/E2, E3/E4, ...)
  const seats = [];
  for (let row = 0; row < ROWS; row++) {
    const rowLabel = String.fromCharCode(65 + row); // A, B, C, D, E
    for (let col = 1; col <= COLS; col++) {
      // Gh·∫ø ƒë√¥i ·ªü h√†ng cu·ªëi c√πng (E), c·ªôt l·∫ª (E1/E2, E3/E4, ...)
      if (row === ROWS - 1 && col % 2 === 1) {
        seats.push({
          label: `${rowLabel}${col}/${rowLabel}${col + 1}`,
          type: 'double',
          key: `${rowLabel}${col}`,
          row: rowLabel
        });
      }
      // B·ªè qua c·ªôt ch·∫µn ·ªü h√†ng cu·ªëi (v√¨ ƒë√£ l√† gh·∫ø ƒë√¥i)
      else if (row === ROWS - 1 && col % 2 === 0) {
        continue;
      }
      // C√°c h√†ng c√≤n l·∫°i l√† gh·∫ø ƒë∆°n
      else if (row < ROWS - 1) {
        seats.push({
          label: `${rowLabel}${col}`,
          type: 'single',
          key: `${rowLabel}${col}`,
          row: rowLabel
        });
      }
    }
  }

  const toggleSeat = (seat) => {
    setError('');
    if (seat.type === 'double') {
      if (selectedSeats.includes(seat.label)) {
        setSelectedSeats(selectedSeats.filter(s => s !== seat.label));
      } else {
        setSelectedSeats([...selectedSeats, seat.label]);
      }
    } else {
      setSelectedSeats((prev) =>
        prev.includes(seat.label)
          ? prev.filter((s) => s !== seat.label)
          : [...prev, seat.label]
      );
    }
  };

  const handleBooking = () => {
    if (selectedSeats.length === 0) {
      setError('Vui l√≤ng ch·ªçn √≠t nh·∫•t 1 gh·∫ø!');
      return;
    }
    navigate(`/payment/${id}`, { state: { seats: selectedSeats } });
  };

  // T·∫°o ma tr·∫≠n gh·∫ø ƒë·ªÉ hi·ªÉn th·ªã t·ª´ng h√†ng
  const seatRows = [];
  let idx = 0;
  for (let row = 0; row < ROWS; row++) {
    const rowSeats = [];
    for (let col = 1; col <= COLS; ) {
      const seat = seats[idx];
      if (!seat) break;
      if (seat.type === 'double') {
        rowSeats.push(seat);
        col += 2;
      } else {
        rowSeats.push(seat);
        col += 1;
      }
      idx++;
    }
    seatRows.push(rowSeats);
  }

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Ch·ªçn ch·ªó ng·ªìi cho phim #{id}</h2>
      <div className="mb-2">
        <span>
          <Button variant="success" size="sm" disabled style={{ marginRight: 5 }} /> Gh·∫ø ƒë∆°n ƒë√£ ch·ªçn
        </span>
        <span className="ms-3">
          <Button variant="warning" size="sm" disabled style={{ marginRight: 5 }} /> Gh·∫ø ƒë√¥i ƒë√£ ch·ªçn
        </span>
        <span className="ms-3">
          <Button variant="outline-light" size="sm" disabled style={{ marginRight: 5, background: '#222' }} /> Gh·∫ø ƒë∆°n tr·ªëng
        </span>
        <span className="ms-3">
          <Button variant="outline-warning" size="sm" disabled style={{ marginRight: 5, background: '#222' }} /> Gh·∫ø ƒë√¥i tr·ªëng
        </span>
      </div>
      {error && <Alert variant="danger">{error}</Alert>}
      <div style={{ display: 'inline-block', background: '#222', padding: 20, borderRadius: 8 }}>
        <div className="text-center mb-2" style={{ color: '#fff' }}>M√†n h√¨nh</div>
        <div>
          {seatRows.map((rowSeats, rowIdx) => (
            <div key={rowIdx} style={{ display: 'flex', justifyContent: 'center', marginBottom: 10 }}>
              {rowSeats.map((seat) => (
                <Button
                  key={seat.key}
                  variant={
                    selectedSeats.includes(seat.label)
                      ? (seat.type === 'double' ? 'warning' : 'success')
                      : (seat.type === 'double' ? 'outline-warning' : 'outline-light')
                  }
                  style={{
                    width: seat.type === 'double' ? 90 : 40,
                    height: 40,
                    padding: 0,
                    marginRight: 8,
                    marginLeft: 0,
                    gridColumn: seat.type === 'double' ? `span 2` : undefined
                  }}
                  onClick={() => toggleSeat(seat)}
                >
                  {seat.type === 'double' ? 'üë´ ' : ''}
                  {seat.label}
                </Button>
              ))}
            </div>
          ))}
        </div>
      </div>
      <div className="mt-3">
        <strong>Gh·∫ø ƒë√£ ch·ªçn:</strong> {selectedSeats.join(', ') || 'Ch∆∞a ch·ªçn'}
      </div>
      <Button className="mt-4" variant="primary" onClick={handleBooking}>
        Ti·∫øp t·ª•c thanh to√°n
      </Button>
    </Container>
  );
};

export default BookingPage;
