// EditMoviePage.js - Cập nhật phim
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5000/api/movies';

const EditMoviePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);

  // 👉 Load dữ liệu phim và định dạng ngày đúng dạng MySQL
  useEffect(() => {
    fetch(`${API_URL}/${id}`)
      .then(res => res.json())
      .then(data => {
        if (data.release_date) {
          const date = new Date(data.release_date);
          const yyyy = date.getFullYear();
          const mm = String(date.getMonth() + 1).padStart(2, '0');
          const dd = String(date.getDate()).padStart(2, '0');
          data.release_date = `${yyyy}-${mm}-${dd}`; // ✅ YYYY-MM-DD
        }
        setMovie(data);
      });
  }, [id]);

  const handleChange = e => {
    const { name, value, files } = e.target;
    setMovie(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleUpdate = async () => {
    const token = localStorage.getItem('token');
    const formData = new FormData();

    Object.entries(movie).forEach(([key, val]) => {
      if (val) formData.append(key, val);
    });

    formData.append('oldImage', movie.image || '');
    formData.append('oldVideo', movie.video || '');

    const res = await fetch(`${API_URL}/${id}`, {
      method: 'PUT',
      headers: { Authorization: `Bearer ${token}` },
      body: formData
    });

    if (res.ok) {
      alert('✅ Cập nhật thành công');
      navigate('/');
    } else {
      alert('❌ Cập nhật thất bại');
    }
  };

  if (!movie) return <p className="container py-4">Đang tải...</p>;

  return (
    <div className="container py-4">
      <h2 className="mb-4 text-primary">📝 Cập nhật phim</h2>
      <div className="row g-2">
        {['title', 'genre', 'director', 'main_actor', 'duration', 'language', 'country'].map(field => (
          <div className="col-md-6" key={field}>
            <input
              className="form-control mb-2"
              name={field}
              value={movie[field] || ''}
              onChange={handleChange}
              placeholder={field.toUpperCase()}
            />
          </div>
        ))}

        <div className="col-md-6">
          <label className="form-label">Ngày khởi chiếu</label>
          <input
            type="date"
            className="form-control mb-2"
            name="release_date"
            value={movie.release_date || ''}
            onChange={handleChange}
          />
        </div>

        {['subtitle', 'age'].map(field => (
          <div className="col-md-6" key={field}>
            <input
              className="form-control mb-2"
              name={field}
              value={movie[field] || ''}
              onChange={handleChange}
              placeholder={field.toUpperCase()}
            />
          </div>
        ))}

        {['description', 'content'].map(field => (
          <div className="col-12" key={field}>
            <textarea
              className="form-control mb-2"
              name={field}
              value={movie[field] || ''}
              onChange={handleChange}
              placeholder={field.toUpperCase()}
              rows={2}
            />
          </div>
        ))}

        <div className="col-md-6">
          <label className="form-label">Cập nhật ảnh mới (nếu có)</label>
          <input type="file" className="form-control mb-2" name="image" onChange={handleChange} />
        </div>
        <div className="col-md-6">
          <label className="form-label">Cập nhật trailer mới (nếu có)</label>
          <input type="file" className="form-control mb-2" name="video" onChange={handleChange} />
        </div>
      </div>

      <button className="btn btn-primary mt-3" onClick={handleUpdate}>💾 Lưu thay đổi</button>
      <button className="btn btn-secondary mt-3 ms-2" onClick={() => navigate('/')}>↩️ Quay lại</button>
    </div>
  );
};

export default EditMoviePage;
