// src/pages/AdminPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPage = () => {
  const [movies, setMovies] = useState([]);
  const [showtimes, setShowtimes] = useState([]);
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    // Fetch all movies, showtimes, and customers
    axios
      .get('http://localhost:5000/api/movies')
      .then((response) => {
        setMovies(response.data);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
      });

    axios
      .get('http://localhost:5000/api/showtimes')
      .then((response) => {
        setShowtimes(response.data);
      })
      .catch((error) => {
        console.error('Error fetching showtimes:', error);
      });

    axios
      .get('http://localhost:5000/api/customers')
      .then((response) => {
        setCustomers(response.data);
      })
      .catch((error) => {
        console.error('Error fetching customers:', error);
      });
  }, []);

  const handleDeleteMovie = (movieId) => {
    axios
      .delete(`http://localhost:5000/api/movies/${movieId}`)
      .then(() => {
        setMovies((prevMovies) => prevMovies.filter((movie) => movie.movie_id !== movieId));
      })
      .catch((error) => {
        console.error('Error deleting movie:', error);
      });
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>

      <h2>Movies</h2>
      <ul>
        {movies.map((movie) => (
          <li key={movie.movie_id}>
            {movie.title} <button onClick={() => handleDeleteMovie(movie.movie_id)}>Delete</button>
          </li>
        ))}
      </ul>

      <h2>Showtimes</h2>
      <ul>
        {showtimes.map((showtime) => (
          <li key={showtime.showtime_id}>
            {showtime.showtime} - {showtime.ticket_price} <button>Delete</button>
          </li>
        ))}
      </ul>

      <h2>Customers</h2>
      <ul>
        {customers.map((customer) => (
          <li key={customer.customer_id}>
            {customer.full_name} - {customer.email} <button>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPage;
