import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Button, Form, Container, Alert, Spinner } from 'react-bootstrap';

const API_URL = 'http://localhost:5000/api/movies';

const EditMoviePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [movie, setMovie] = useState(null);
  const [image, setImage] = useState(null);
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get(`${API_URL}/${id}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(() => {
        setMessage('Không tìm thấy phim.');
        setLoading(false);
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMovie(prev => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const formData = new FormData();
      Object.entries(movie).forEach(([key, val]) => {
        if (val !== null && val !== undefined) formData.append(key, val);
      });
      if (image) formData.append('image', image);
      if (video) formData.append('video', video);

      const res = await axios.put(`${API_URL}/${id}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });

      setMessage(res.data.message || 'Cập nhật thành công');
      navigate('/'); // hoặc về admin nếu bạn thích
    } catch (err) {
      console.error(err);
      setMessage('Cập nhật thất bại');
    }
  };

  if (loading) return <Container className="mt-4"><Spinner animation="border" /></Container>;
  if (!movie) return <Container className="mt-4"><Alert variant="danger">{message}</Alert></Container>;

  return (
    <Container className="mt-5">
      <h2 className="mb-4 text-center">🛠️ Sửa Phim</h2>
      {message && <Alert variant="info">{message}</Alert>}
      <Form onSubmit={handleUpdate} encType="multipart/form-data">
        <Form.Group className="mb-3">
          <Form.Label>Tiêu đề</Form.Label>
          <Form.Control name="title" value={movie.title || ''} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Thể loại</Form.Label>
          <Form.Control name="genre" value={movie.genre || ''} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Đạo diễn</Form.Label>
          <Form.Control name="director" value={movie.director || ''} onChange={handleChange} required />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Diễn viên chính</Form.Label>
          <Form.Control name="main_actor" value={movie.main_actor || ''} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Thời lượng (phút)</Form.Label>
          <Form.Control type="number" name="duration" value={movie.duration || ''} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Ngôn ngữ</Form.Label>
          <Form.Control name="language" value={movie.language || ''} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Quốc gia</Form.Label>
          <Form.Control name="country" value={movie.country || ''} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Ngày khởi chiếu</Form.Label>
          <Form.Control type="date" name="release_date" value={movie.release_date?.substring(0, 10) || ''} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Mô tả</Form.Label>
          <Form.Control as="textarea" name="description" value={movie.description || ''} onChange={handleChange} rows={3} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Phụ đề</Form.Label>
          <Form.Control name="subtitle" value={movie.subtitle || ''} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Giới hạn tuổi</Form.Label>
          <Form.Control type="number" name="age" value={movie.age || ''} onChange={handleChange} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Nội dung</Form.Label>
          <Form.Control as="textarea" name="content" value={movie.content || ''} onChange={handleChange} rows={4} />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Ảnh đại diện (Poster)</Form.Label>
          <Form.Control type="file" accept="image/*" onChange={(e) => setImage(e.target.files[0])} />
        </Form.Group>

        <Form.Group className="mb-4">
          <Form.Label>Video Trailer (mp4)</Form.Label>
          <Form.Control type="file" accept="video/mp4" onChange={(e) => setVideo(e.target.files[0])} />
        </Form.Group>

        <div className="text-center">
          <Button type="submit" variant="primary">Lưu</Button>
          <Button variant="secondary" className="ms-3" onClick={() => navigate(-1)}>Hủy</Button>
        </div>
      </Form>
    </Container>
  );
};

export default EditMoviePage;
