// src/pages/HomePage.js
import React, { useState, useEffect } from 'react';

const HomePage = () => {
  const [query, setQuery] = useState('');
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [showtimes, setShowtimes] = useState([]);
  const [selectedShowtime, setSelectedShowtime] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [message, setMessage] = useState('');

  const searchMovies = async () => {
    try {
      const res = await fetch(`/api/movies/search?query=${query}`);
      const data = await res.json();
      setMovies(data);
    } catch (error) {
      console.error('Search failed:', error);
    }
  };

  const fetchShowtimes = async (movieId) => {
    try {
      const res = await fetch(`/api/showtimes/movie/${movieId}`);
      const data = await res.json();
      setShowtimes(data);
    } catch (error) {
      console.error('Fetch showtimes failed:', error);
    }
  };

  const handleSeatToggle = (seat) => {
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const handleBooking = async () => {
    const token = localStorage.getItem('token');
    if (!selectedShowtime || selectedSeats.length === 0) {
      return alert('Vui l√≤ng ch·ªçn su·∫•t chi·∫øu v√† gh·∫ø.');
    }
    try {
      const res = await fetch('/api/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          showtime_id: selectedShowtime.id,
          seats: selectedSeats,
        }),
      });
      const result = await res.json();
      if (res.ok) {
        setMessage('ƒê·∫∑t v√© th√†nh c√¥ng!');
        setSelectedMovie(null);
        setSelectedShowtime(null);
        setSelectedSeats([]);
      } else {
        setMessage(result.message || 'ƒê·∫∑t v√© th·∫•t b·∫°i.');
      }
    } catch (err) {
      setMessage('L·ªói k·∫øt n·ªëi khi ƒë·∫∑t v√©.');
    }
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>üé¨ T√¨m ki·∫øm & ƒê·∫∑t v√©</h2>
      <input value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Nh·∫≠p t√™n phim..." />
      <button onClick={searchMovies}>T√¨m</button>

      {movies.length > 0 && (
        <ul>
          {movies.map((movie) => (
            <li key={movie.id} onClick={() => {
              setSelectedMovie(movie);
              fetchShowtimes(movie.id);
            }}>
              {movie.title}
            </li>
          ))}
        </ul>
      )}

      {showtimes.length > 0 && (
        <div>
          <h3>Su·∫•t chi·∫øu</h3>
          {showtimes.map((show) => (
            <div key={show.id} onClick={() => setSelectedShowtime(show)}>
              {show.date} - {show.time}
            </div>
          ))}
        </div>
      )}

      {selectedShowtime && (
        <div>
          <h4>Ch·ªçn gh·∫ø:</h4>
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            {[...Array(20)].map((_, i) => {
              const seat = i + 1;
              return (
                <button
                  key={seat}
                  onClick={() => handleSeatToggle(seat)}
                  style={{
                    background: selectedSeats.includes(seat) ? 'green' : 'gray',
                    color: 'white',
                  }}
                >
                  {seat}
                </button>
              );
            })}
          </div>
          <button onClick={handleBooking} style={{ marginTop: '1rem' }}>ƒê·∫∑t v√©</button>
        </div>
      )}

      {message && <p>{message}</p>}
    </div>
  );
};

export default HomePage;
