import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Container, Button, Spinner, Alert, Row, Col, Card } from 'react-bootstrap';

const MovieDetailPage = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/movies/${id}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Không tìm thấy phim!');
        setLoading(false);
      });
  }, [id]);

  if (loading) return <Spinner animation="border" className="d-block mx-auto mt-5" />;
  if (error) return <Alert variant="danger" className="mt-5 text-center">{error}</Alert>;
  if (!movie) return null;

  return (
    <Container className="mt-4">
      <Row className="justify-content-center">
        <Col md={11} lg={10}>
          <Card className="shadow-lg border-0 p-3" style={{ background: "#f8f9fa" }}>
            <Row>
              {/* Poster bên trái */}
              <Col md={4} className="d-flex flex-column align-items-center justify-content-center mb-3 mb-md-0">
                <div style={{
                  width: '100%',
                  height: 400,
                  overflow: 'hidden',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  borderRadius: 12,
                  background: '#fff',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.07)'
                }}>
                  <img
                    src={movie.image ? `http://localhost:5000${movie.image}` : "https://via.placeholder.com/400x400?text=Movie+Poster"}
                    alt={movie.title}
                    style={{ maxHeight: 400, width: 'auto', objectFit: 'contain' }}
                  />
                </div>
              </Col>
              {/* Video trailer và thông tin bên phải */}
              <Col md={8}>
                {/* Video trailer nằm trên */}
                {movie.video && (
                  <div className="mb-4">
                    <h5 className="text-center mb-2">Trailer</h5>
                    <video
                      width="100%"
                      height="340"
                      controls
                      style={{ borderRadius: 10, background: '#000', boxShadow: '0 2px 8px rgba(0,0,0,0.07)' }}
                    >
                      <source src={`http://localhost:5000${movie.video}`} type="video/mp4" />
                      Trình duyệt không hỗ trợ video.
                    </video>
                  </div>
                )}
                {/* Thông tin phim nằm dưới video */}
                <div className="p-3" style={{ background: "#fff", borderRadius: 10, boxShadow: '0 1px 4px rgba(0,0,0,0.05)' }}>
                  <h2 className="mb-3 text-primary text-center">{movie.title}</h2>
                  <p><strong>Thể loại:</strong> {movie.genre}</p>
                  <p><strong>Đạo diễn:</strong> {movie.director}</p>
                  <p><strong>Khởi chiếu:</strong> {movie.release_date ? new Date(movie.release_date).toLocaleDateString() : ''}</p>
                  <p><strong>Mô tả:</strong> {movie.description || 'Chưa có mô tả.'}</p>
                  <div className="mt-4 d-flex flex-wrap gap-3 justify-content-center">
                    <Button variant="primary" onClick={() => navigate(`/booking/${movie.movie_id}`)}>
                      Đặt vé
                    </Button>
                    <Button variant="secondary" onClick={() => navigate(-1)}>
                      Quay lại
                    </Button>
                  </div>
                </div>
              </Col>
            </Row>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default MovieDetailPage;
