import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Card, Button, Form, Alert } from 'react-bootstrap';

const PaymentPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [name, setName] = React.useState('');
  const [card, setCard] = React.useState('');
  const [success, setSuccess] = React.useState(false);
  const [error, setError] = React.useState('');

  const handlePay = (e) => {
    e.preventDefault();
    setError('');
    if (!name || !card) {
      setError('Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin.');
      return;
    }
    // Gi·∫£ l·∫≠p thanh to√°n th√†nh c√¥ng
    setTimeout(() => {
      setSuccess(true);
    }, 1000);
  };

  return (
    <Container className="py-5 d-flex flex-column align-items-center justify-content-center" style={{ minHeight: "80vh" }}>
      <Card className="shadow p-4" style={{ maxWidth: 420, width: "100%" }}>
        <h2 className="mb-3 text-center" style={{ fontWeight: 700 }}>Thanh to√°n cho phim #{id}</h2>
        <p className="text-center text-secondary mb-4">Vui l√≤ng nh·∫≠p th√¥ng tin ƒë·ªÉ ho√†n t·∫•t thanh to√°n v√© xem phim.</p>
        {success ? (
          <Alert variant="success" className="text-center">
            Thanh to√°n th√†nh c√¥ng! <br /> Ch√∫c b·∫°n xem phim vui v·∫ª üéâ
            <div className="mt-3">
              <Button variant="primary" onClick={() => navigate('/')}>V·ªÅ trang ch·ªß</Button>
            </div>
          </Alert>
        ) : (
          <Form onSubmit={handlePay}>
            <Form.Group className="mb-3">
              <Form.Label>H·ªç v√† t√™n</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nh·∫≠p h·ªç t√™n"
                value={name}
                onChange={e => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>S·ªë th·∫ª ng√¢n h√†ng</Form.Label>
              <Form.Control
                type="text"
                placeholder="Nh·∫≠p s·ªë th·∫ª"
                value={card}
                onChange={e => setCard(e.target.value)}
                maxLength={19}
              />
            </Form.Group>
            {error && <Alert variant="danger">{error}</Alert>}
            <Button type="submit" variant="success" className="w-100 fw-bold">
              Thanh to√°n
            </Button>
          </Form>
        )}
      </Card>
    </Container>
  );
};

export default PaymentPage;
