import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Button, Alert } from 'react-bootstrap';

// C·∫•u tr√∫c gh·∫ø
const seatLayout = {
  A: 18, B: 20, C: 24, D: 24, E: 24, F: 24,
  G: 24, H: 24, J: 24, K: 24,
  L: [2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2] // gh·∫ø ƒë√¥i
};

const rowLabels = Object.keys(seatLayout);

const BookingPage = () => {
  const { id } = useParams(); // id showtime
  const navigate = useNavigate();
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [error, setError] = useState('');

  const getSeats = () => {
    const seats = [];
    rowLabels.forEach(row => {
      if (row === 'L') {
        let seatNum = 1;
        for (let pair of seatLayout[row]) {
          seats.push({
            label: `${row}${seatNum}/${row}${seatNum + 1}`,
            type: 'double',
            row,
            key: `${row}${seatNum}`
          });
          seatNum += 2;
        }
      } else {
        for (let i = 1; i <= seatLayout[row]; i++) {
          seats.push({
            label: `${row}${i}`,
            type: 'single',
            row,
            key: `${row}${i}`
          });
        }
      }
    });
    return seats;
  };

  const seats = getSeats();

  const seatRows = {};
  seats.forEach(seat => {
    if (!seatRows[seat.row]) seatRows[seat.row] = [];
    seatRows[seat.row].push(seat);
  });

  const total = selectedSeats.reduce((sum, seat) => {
    return sum + (seat.includes('/') ? 150000 : 55000);
  }, 0);

  const toggleSeat = (seat) => {
    setError('');
    setSelectedSeats(prev =>
      prev.includes(seat.label)
        ? prev.filter(s => s !== seat.label)
        : [...prev, seat.label]
    );
  };

  const handleBooking = () => {
    if (selectedSeats.length === 0) {
      setError('Vui l√≤ng ch·ªçn √≠t nh·∫•t 1 gh·∫ø!');
      return;
    }

    const token = localStorage.getItem('token');
    const customerId = 1; // üëâ Thay b·∫±ng ID th·ª±c t·∫ø n·∫øu c√≥ h·ªá th·ªëng ƒëƒÉng nh·∫≠p

    Promise.all(
      selectedSeats.map(label => {
        const first = label.split('/')[0];
        const row = first.charAt(0);
        const column = parseInt(first.slice(1));
        const price = label.includes('/') ? 150000 : 55000;

        return fetch('http://localhost:5000/api/tickets', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`
          },
          body: JSON.stringify({
            showtime_id: parseInt(id),
            customer_id: customerId,
            seat_row: row,
            seat_column: column,
            price,
            status: 'Booked'
          })
        });
      })
    )
      .then(() => {
        navigate(`/payment/${id}`, { state: { seats: selectedSeats } });
      })
      .catch(() => {
        setError('L·ªói khi t·∫°o v√©. Vui l√≤ng th·ª≠ l·∫°i!');
      });
  };

  return (
    <Container className="mt-4">
      <h2 className="text-center mb-4">Ch·ªçn ch·ªó ng·ªìi cho phim</h2>
      <div className="d-flex justify-content-center">
        <div style={{ background: '#222', padding: 20, borderRadius: 8, minWidth: 900 }}>
          <div className="text-center mb-2" style={{ color: '#fff', fontWeight: 'bold' }}>M√†n h√¨nh</div>
          <div className="text-center mb-3 text-white">Khu v·ª±c xem t·ªët nh·∫•t</div>

          {rowLabels.map(row => (
            <div key={row} className="d-flex align-items-center justify-content-center mb-2">
              <span style={{ width: 20, color: '#fff', marginRight: 6 }}>{row}</span>
              {seatRows[row].map(seat => (
                <Button
                  key={seat.key}
                  variant={
                    selectedSeats.includes(seat.label)
                      ? (seat.type === 'double' ? 'warning' : 'success')
                      : (seat.type === 'double' ? 'outline-warning' : 'outline-light')
                  }
                  style={{
                    width: seat.type === 'double' ? 80 : 36,
                    height: 36,
                    fontSize: 13,
                    marginRight: 6
                  }}
                  onClick={() => toggleSeat(seat)}
                >
                  {seat.type === 'double' ? 'üë¨ ' : ''}
                  {seat.label}
                </Button>
              ))}
            </div>
          ))}
        </div>
      </div>

      {error && <Alert variant="danger" className="mt-3 text-center">{error}</Alert>}

      <div className="mt-3 text-center">
        <strong>Gh·∫ø ƒë√£ ch·ªçn:</strong> {selectedSeats.join(', ') || 'Ch∆∞a ch·ªçn'}
        <br />
        <strong>T·ªïng ti·ªÅn:</strong> {total.toLocaleString('vi-VN')} VND
      </div>

      <div className="d-flex justify-content-center gap-4 mt-4">
        <Button variant="primary" onClick={handleBooking}>Ti·∫øp t·ª•c thanh to√°n</Button>
        <Button variant="secondary" onClick={() => navigate(-1)}>Quay l·∫°i</Button>
      </div>
    </Container>
  );
};

export default BookingPage;
