import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Container, Button, Spinner, Alert, Row, Col, Card } from 'react-bootstrap';
import { FaRegLightbulb, FaLightbulb } from 'react-icons/fa';

const MovieDetailPage = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [darkMode, setDarkMode] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/movies/${id}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Không tìm thấy phim!');
        setLoading(false);
      });
  }, [id]);

  const themeBg = darkMode ? "#23272f" : "#f8f9fa";
  const themeText = darkMode ? "#fff" : "#222";
  const cardBg = darkMode ? "#181a20" : "#fff";
  const cardShadow = darkMode ? "0 2px 16px rgba(0,0,0,0.5)" : "0 2px 8px rgba(0,0,0,0.07)";

  if (loading) return <Spinner animation="border" className="d-block mx-auto mt-5" />;
  if (error) return <Alert variant="danger" className="mt-5 text-center">{error}</Alert>;
  if (!movie) return null;

  return (
    <div
      style={{
        background: themeBg,
        minHeight: "100vh",
        color: themeText,
        transition: "all 0.3s"
      }}
    >
      {/* Nút bóng đèn chuyển theme */}
      <div style={{
        position: 'fixed',
        top: 24,
        right: 36,
        zIndex: 1000,
        cursor: 'pointer',
        fontSize: 28,
        color: darkMode ? "#ffd600" : "#222"
      }}
        title={darkMode ? "Chuyển sang chế độ sáng" : "Chuyển sang chế độ tối"}
        onClick={() => setDarkMode(!darkMode)}
      >
        {darkMode ? <FaLightbulb /> : <FaRegLightbulb />}
      </div>

      <Container className="mt-4">
        {/* Tiêu đề căn giữa */}
        <div className="mb-4">
          <h2 className="text-center" style={{ letterSpacing: 1 }}>Chi tiết phim</h2>
        </div>
        <Row className="justify-content-center">
          <Col md={11} lg={10}>
            <Card
              className="shadow-lg border-0 p-3"
              style={{
                background: cardBg,
                color: themeText,
                transition: "all 0.3s",
                boxShadow: cardShadow
              }}
            >
              <Row>
                {/* Poster bên trái phía trên */}
                <Col md={4} className="mb-3 mb-md-0">
                  <div style={{
                    width: '100%',
                    height: 340,
                    overflow: 'hidden',
                    alignItems: 'flex-start',
                    justifyContent: 'flex-start',
                    borderRadius: 12,
                    background: darkMode ? "#23272f" : "#fff",
                    boxShadow: cardShadow
                  }}>
                    <img
                      src={movie.image ? `http://localhost:5000${movie.image}` : "https://via.placeholder.com/400x400?text=Movie+Poster"}
                      alt={movie.title}
                      style={{ maxHeight: 340, width: '100%', objectFit: 'contain', display: 'block', margin: '0 auto' }}
                    />
                  </div>
                </Col>
                {/* Video trailer và thông tin bên phải */}
                <Col md={8}>
                  {/* Video trailer nằm trên */}
                  {movie.video && (
                    <div className="mb-4">
                      <h5 className="text-center mb-2"> Video Trailer</h5>
                      <video
                        width="100%"
                        height="400"
                        controls
                        style={{
                          borderRadius: 10,
                          background: '#000',
                          boxShadow: cardShadow,
                          display: 'block'
                        }}
                      >
                        <source src={`http://localhost:5000${movie.video}`} type="video/mp4" />
                        Trình duyệt không hỗ trợ video.
                      </video>
                    </div>
                  )}
                  {/* Thông tin phim nằm dưới video */}
                  <div className="p-3" style={{
                    background: cardBg,
                    borderRadius: 10,
                    boxShadow: '0 1px 4px rgba(0,0,0,0.05)'
                  }}>
                    <h2 className="mb-3 text-primary text-center">{movie.title}</h2>
                    <p><strong>Thể loại:</strong> {movie.genre}</p>
                    <p><strong>Đạo diễn:</strong> {movie.director}</p>
                    <p><strong>Khởi chiếu:</strong> {movie.release_date ? new Date(movie.release_date).toLocaleDateString() : ''}</p>
                    <p><strong>Mô tả:</strong> {movie.description || 'Chưa có mô tả.'}</p>
                    <div className="mt-4 d-flex flex-wrap gap-3 justify-content-center">
                      <Button variant="primary" onClick={() => navigate(`/booking/${movie.movie_id}`)}>
                        Đặt vé
                      </Button>
                      <Button variant="secondary" onClick={() => navigate(-1)}>
                        Quay lại
                      </Button>
                    </div>
                  </div>
                </Col>
              </Row>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default MovieDetailPage;
