import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const API_URL = 'http://localhost:5000/api/movies';

const AdminPage = () => {
  const [movies, setMovies] = useState([]);
  const [newMovie, setNewMovie] = useState({
    title: '',
    genre: '',
    director: '',
    duration: '',
    release_date: '',
    description: '',
    subtitle: '',
    age: '',
    content: '',
    image: null,
    video: null
  });
  const [editingMovie, setEditingMovie] = useState(null);
  const navigate = useNavigate();

  // Chặn user thường truy cập trang admin
  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (!token || role !== 'Admin') {
      navigate('/');
    } else {
      fetchMovies();
    }
    // eslint-disable-next-line
  }, [navigate]);

  const fetchMovies = async () => {
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setMovies(Array.isArray(data) ? data : []);
    } catch (err) {
      alert('Lỗi khi lấy danh sách phim');
      setMovies([]);
    }
  };

  const handleAddMovie = async () => {
    const { title, genre, director, duration, release_date, description, subtitle, age, content } = newMovie;
    if (!title || !genre || !director || !duration || !release_date || !description || !subtitle || !age || !content) {
      alert("Vui lòng nhập đầy đủ thông tin.");
      return;
    }

    const token = localStorage.getItem('token');
    const formData = new FormData();
    Object.entries(newMovie).forEach(([key, val]) => {
      if (val) formData.append(key, val);
    });

    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      const data = await res.json();
      if (res.ok) {
        alert('Thêm phim thành công');
        setNewMovie({
          title: '', genre: '', director: '', duration: '', release_date: '', description: '', subtitle: '', age: '', content: '', image: null, video: null
        });
        fetchMovies();
      } else {
        alert(data.error || 'Lỗi khi thêm phim');
      }
    } catch (err) {
      alert('Lỗi khi gửi yêu cầu');
    }
  };

  const handleUpdateMovie = async () => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    Object.entries(editingMovie).forEach(([key, val]) => {
      if (val) formData.append(key, val);
    });

    const res = await fetch(`${API_URL}/${editingMovie.movie_id}`, {
      method: 'PUT',
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    const data = await res.json();
    if (res.ok) {
      alert('Cập nhật thành công');
      setEditingMovie(null);
      fetchMovies();
    } else {
      alert(data.error || 'Cập nhật thất bại');
    }
  };

  const handleDeleteMovie = async (id) => {
    const token = localStorage.getItem('token');
    if (!window.confirm('Bạn có chắc muốn xóa phim này?')) return;

    const res = await fetch(`${API_URL}/${id}`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    if (res.ok) fetchMovies();
    else alert('Xóa thất bại');
  };

  return (
    <div className="container py-4">
      <h2 className="mb-4">🎬 Quản lý phim</h2>

      <div className="border rounded p-3 mb-5">
        <h5>Thêm phim mới</h5>
        <div className="row g-2">
          <div className="col-md-6">
            <input className="form-control mb-2" placeholder="Tên phim" value={newMovie.title} onChange={e => setNewMovie({ ...newMovie, title: e.target.value })} />
            <input className="form-control mb-2" placeholder="Thể loại" value={newMovie.genre} onChange={e => setNewMovie({ ...newMovie, genre: e.target.value })} />
            <input className="form-control mb-2" placeholder="Đạo diễn" value={newMovie.director} onChange={e => setNewMovie({ ...newMovie, director: e.target.value })} />
            <input className="form-control mb-2" placeholder="Thời lượng (phút)" value={newMovie.duration} onChange={e => setNewMovie({ ...newMovie, duration: e.target.value })} />
            <input className="form-control mb-2" placeholder="Phụ đề" value={newMovie.subtitle} onChange={e => setNewMovie({ ...newMovie, subtitle: e.target.value })} />
            <input className="form-control mb-2" placeholder="Độ tuổi (VD: 16+)" value={newMovie.age} onChange={e => setNewMovie({ ...newMovie, age: e.target.value })} />
          </div>
          <div className="col-md-6">
            <input type="date" className="form-control mb-2" value={newMovie.release_date} onChange={e => setNewMovie({ ...newMovie, release_date: e.target.value })} />
            <textarea className="form-control mb-2" placeholder="Mô tả" value={newMovie.description} onChange={e => setNewMovie({ ...newMovie, description: e.target.value })} rows={2} />
            <textarea className="form-control mb-2" placeholder="Nội dung phim" value={newMovie.content} onChange={e => setNewMovie({ ...newMovie, content: e.target.value })} rows={3} />
            <input type="file" className="form-control mb-2" accept="image/*" onChange={e => setNewMovie({ ...newMovie, image: e.target.files[0] })} />
            <input type="file" className="form-control mb-2" accept="video/*" onChange={e => setNewMovie({ ...newMovie, video: e.target.files[0] })} />
          </div>
        </div>
        <button className="btn btn-success mt-3" onClick={handleAddMovie}>➕ Thêm phim</button>
      </div>

      <div className="row">
        {movies.map(movie => (
          <div key={movie.movie_id} className="col-md-6 mb-4">
            <div className="card h-100">
              {editingMovie?.movie_id === movie.movie_id ? (
                <div className="card-body">
                  <input className="form-control mb-2" value={editingMovie.title} onChange={(e) => setEditingMovie({ ...editingMovie, title: e.target.value })} />
                  <input className="form-control mb-2" value={editingMovie.genre} onChange={(e) => setEditingMovie({ ...editingMovie, genre: e.target.value })} />
                  <input className="form-control mb-2" value={editingMovie.director} onChange={(e) => setEditingMovie({ ...editingMovie, director: e.target.value })} />
                  <input className="form-control mb-2" value={editingMovie.duration} onChange={(e) => setEditingMovie({ ...editingMovie, duration: e.target.value })} />
                  <input className="form-control mb-2" value={editingMovie.subtitle} onChange={(e) => setEditingMovie({ ...editingMovie, subtitle: e.target.value })} />
                  <input className="form-control mb-2" value={editingMovie.age} onChange={(e) => setEditingMovie({ ...editingMovie, age: e.target.value })} />
                  <input type="date" className="form-control mb-2" value={editingMovie.release_date} onChange={(e) => setEditingMovie({ ...editingMovie, release_date: e.target.value })} />
                  <textarea className="form-control mb-2" value={editingMovie.description} onChange={(e) => setEditingMovie({ ...editingMovie, description: e.target.value })} rows={2} />
                  <textarea className="form-control mb-2" value={editingMovie.content} onChange={(e) => setEditingMovie({ ...editingMovie, content: e.target.value })} rows={3} />
                  <input type="file" className="form-control mb-2" onChange={(e) => setEditingMovie({ ...editingMovie, image: e.target.files[0] })} />
                  <input type="file" className="form-control mb-2" onChange={(e) => setEditingMovie({ ...editingMovie, video: e.target.files[0] })} />
                  <button className="btn btn-primary me-2" onClick={handleUpdateMovie}>💾 Lưu</button>
                  <button className="btn btn-secondary" onClick={() => setEditingMovie(null)}>❌ Hủy</button>
                </div>
              ) : (
                <>
                  {movie.image && <img src={`http://localhost:5000${movie.image}`} className="card-img-top" alt="poster" />}
                  <div className="card-body">
                    <h5 className="card-title">{movie.title}</h5>
                    <p className="card-text">🎭 Thể loại: {movie.genre}</p>
                    <p className="card-text">🎬 Đạo diễn: {movie.director}</p>
                    <p className="card-text">⏱ Thời lượng: {movie.duration} phút</p>
                    <p className="card-text">🈂️ Phụ đề: {movie.subtitle}</p>
                    <p className="card-text">🔞 Độ tuổi: {movie.age}</p>
                    <p className="card-text">📅 Khởi chiếu: {movie.release_date ? new Date(movie.release_date).toLocaleDateString() : ""}</p>
                    <p className="card-text">📝 Mô tả: {movie.description}</p>
                    <p className="card-text">📖 Nội dung: {movie.content}</p>
                    {movie.video && (
                      <video width="100%" height="200" controls className="my-2">
                        <source src={`http://localhost:5000${movie.video}`} type="video/mp4" />
                        Trình duyệt không hỗ trợ video.
                      </video>
                    )}
                    <button className="btn btn-warning me-2" onClick={() => setEditingMovie(movie)}>✏️ Sửa</button>
                    <button className="btn btn-danger" onClick={() => handleDeleteMovie(movie.movie_id)}>🗑️ Xóa</button>
                  </div>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPage;
