import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminPage = () => {
  const [movies, setMovies] = useState([]);
  const [newMovie, setNewMovie] = useState({
    title: '',
    genre: '',
    director: '',
    release_date: '',
    image: null,
    video: null
  });
  const [editingMovie, setEditingMovie] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const role = localStorage.getItem('role');
    if (role?.toLowerCase() !== 'admin') {
      navigate('/');
    } else {
      fetchMovies();
    }
  }, [navigate]);

  const fetchMovies = async () => {
    try {
      const res = await fetch('/api/movies');
      const data = await res.json();
      setMovies(data);
    } catch (err) {
      console.error('Lỗi khi lấy danh sách phim:', err);
    }
  };

  const handleAddMovie = async () => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    formData.append('title', newMovie.title);
    formData.append('genre', newMovie.genre);
    formData.append('director', newMovie.director);
    formData.append('release_date', newMovie.release_date);
    if (newMovie.image) formData.append('image', newMovie.image);
    if (newMovie.video) formData.append('video', newMovie.video);

    const res = await fetch('/api/movies', {
      method: 'POST',
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    if (res.ok) {
      setNewMovie({ title: '', genre: '', director: '', release_date: '', image: null, video: null });
      fetchMovies();
    }
  };

  const handleUpdateMovie = async () => {
    const token = localStorage.getItem('token');
    const formData = new FormData();
    formData.append('title', editingMovie.title);
    formData.append('genre', editingMovie.genre);
    formData.append('director', editingMovie.director);
    formData.append('release_date', editingMovie.release_date);
    if (editingMovie.image) formData.append('image', editingMovie.image);
    if (editingMovie.video) formData.append('video', editingMovie.video);

    const res = await fetch(`/api/movies/${editingMovie.movie_id}`, {
      method: 'PUT',
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: formData,
    });

    if (res.ok) {
      setEditingMovie(null);
      fetchMovies();
    }
  };

  const handleDeleteMovie = async (id) => {
    const token = localStorage.getItem('token');
    const res = await fetch(`/api/movies/${id}`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    if (res.ok) fetchMovies();
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Quản lý phim (Admin)</h2>

      <div>
        <input value={newMovie.title} onChange={(e) => setNewMovie({ ...newMovie, title: e.target.value })} placeholder="Tên phim" />
        <input value={newMovie.genre} onChange={(e) => setNewMovie({ ...newMovie, genre: e.target.value })} placeholder="Thể loại" />
        <input value={newMovie.director} onChange={(e) => setNewMovie({ ...newMovie, director: e.target.value })} placeholder="Đạo diễn" />
        <input type="date" value={newMovie.release_date} onChange={(e) => setNewMovie({ ...newMovie, release_date: e.target.value })} />
        <input type="file" accept="image/*" onChange={(e) => setNewMovie({ ...newMovie, image: e.target.files[0] })} />
        <input type="file" accept="video/*" onChange={(e) => setNewMovie({ ...newMovie, video: e.target.files[0] })} />
        <button onClick={handleAddMovie}>Thêm phim</button>
      </div>

      <ul style={{ marginTop: '2rem', listStyle: 'none', padding: 0 }}>
        {movies.map((movie) => (
          <li key={movie.movie_id} style={{ border: '1px solid #ccc', padding: '1rem', marginBottom: '1rem' }}>
            {editingMovie && editingMovie.movie_id === movie.movie_id ? (
              <>
                <input value={editingMovie.title} onChange={(e) => setEditingMovie({ ...editingMovie, title: e.target.value })} />
                <input value={editingMovie.genre} onChange={(e) => setEditingMovie({ ...editingMovie, genre: e.target.value })} />
                <input value={editingMovie.director} onChange={(e) => setEditingMovie({ ...editingMovie, director: e.target.value })} />
                <input type="date" value={editingMovie.release_date} onChange={(e) => setEditingMovie({ ...editingMovie, release_date: e.target.value })} />
                <input type="file" accept="image/*" onChange={(e) => setEditingMovie({ ...editingMovie, image: e.target.files[0] })} />
                <input type="file" accept="video/*" onChange={(e) => setEditingMovie({ ...editingMovie, video: e.target.files[0] })} />
                <button onClick={handleUpdateMovie}>Lưu</button>
                <button onClick={() => setEditingMovie(null)}>Hủy</button>
              </>
            ) : (
              <>
                <h3>{movie.title}</h3>
                <p><strong>Thể loại:</strong> {movie.genre}</p>
                <p><strong>Đạo diễn:</strong> {movie.director}</p>
                <p><strong>Khởi chiếu:</strong> {new Date(movie.release_date).toLocaleDateString()}</p>

                {movie.image && <img src={movie.image} alt="poster" style={{ maxWidth: '200px', display: 'block', marginBottom: '1rem' }} />}
                {movie.video && (
                  <video width="320" height="180" controls>
                    <source src={movie.video} type="video/mp4" />
                    Trình duyệt không hỗ trợ phát video.
                  </video>
                )}

                <div style={{ marginTop: '1rem' }}>
                  <button onClick={() => setEditingMovie(movie)}>Sửa</button>
                  <button onClick={() => handleDeleteMovie(movie.movie_id)} style={{ marginLeft: '1rem' }}>Xóa</button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPage;
